// src/Quiz.js
//import quizData from '/Users/sarthakjha/Uni/projects/quizapp/Quiz-Website/src/quiz.json';

import React, { useEffect, useState } from 'react';
import quizData from '/Users/sarthakjha/Uni/projects/quizapp/Quiz-Website/src/quiz.json';

const StartPage = ({ onStart }) => {
  const [isFullScreen, setIsFullScreen] = useState(false);

  // Check if full-screen mode is enabled
  useEffect(() => {
    const handleFullScreenChange = () => {
      setIsFullScreen(document.fullscreenElement !== null);
    };

    document.addEventListener('fullscreenchange', handleFullScreenChange);

    return () => {
      document.removeEventListener('fullscreenchange', handleFullScreenChange);
    };
  }, []);

  // Request full-screen mode
  const requestFullScreen = () => {
    const element = document.documentElement;
    if (element.requestFullscreen) {
      element.requestFullscreen();
    } else if (element.mozRequestFullScreen) { /* Firefox */
      element.mozRequestFullScreen();
    } else if (element.webkitRequestFullscreen) { /* Chrome, Safari and Opera */
      element.webkitRequestFullscreen();
    } else if (element.msRequestFullscreen) { /* IE/Edge */
      element.msRequestFullscreen();
    }
  };

  const handleStartQuiz = () => {
    // Check if full-screen mode is enabled
    if (isFullScreen) {
      onStart();
    } else {
      // Prompt user to enter full-screen mode
      requestFullScreen();
    }
  };

  return (
    <div>
      {!isFullScreen && (
        <div className="fullscreen-popup">
          <p>Please enter full-screen mode to start the quiz.</p>
          <button onClick={requestFullScreen}>Enter Full-Screen</button>
        </div>
      )}
      <h1>Welcome to the Country Capitals Quiz</h1>
      <button onClick={handleStartQuiz}>Start Quiz</button>
    </div>
  );
};

export default StartPage;
